mapper.select:
SELECT studno, student.name, student.userid, grade, idnum, birthdate, tel, height, weight, student.deptno, department.dname, student.profno, professor.name as pname FROM student JOIN department ON student.deptno=department.deptno JOIN professor ON student.profno=professor.profno
student.name LIKE concat('%', #{name}, '%')
ORDER BY studno DESC LIMIT #{limitStart}, #{listCount}
